edit.find:
if ($sort_days)
{
	$min_post_time = time() - ($sort_days * 86400);

	$sql = 'SELECT COUNT(post_id) AS num_posts
		FROM ' . POSTS_TABLE . "
		WHERE topic_id = $topic_id
			AND post_time >= $min_post_time
			$usort_where
		" . (($auth->acl_get('m_approve', $forum_id)) ? '' : 'AND post_approved = 1');
	$result = $db->sql_query($sql);
	$total_posts = (int) $db->sql_fetchfield('num_posts');
	$db->sql_freeresult($result);

	$limit_posts_time = "AND p.post_time >= $min_post_time ";

	if (isset($_POST['sort']))
	{
		$start = 0;
	}
}

edit.action:
//----[ User Post Isolation ]----\\
elseif(isset($_POST['user_sort']))
{
	$s_sort_user_id = request_var('user_select', (int) 0);

	$sql = 'SELECT COUNT(post_id) AS num_posts
		FROM ' . POSTS_TABLE . "
		WHERE topic_id = $topic_id
			AND poster_id = $s_sort_user_id
		" . (($auth->acl_get('m_approve', $forum_id)) ? '' : 'AND post_approved = 1');
	$result = $db->sql_query($sql);
	$total_posts = (int) $db->sql_fetchfield('num_posts');
	$db->sql_freeresult($result);

	$limit_posts_time = '';
}
//----[ End User Post Isolation ]----\\

//----[ User Post Isolation ]----\\
	$sql = 'SELECT DISTINCT p.poster_id AS poster_id, u.username_clean AS username_clean
		FROM ' . POSTS_TABLE . ' p, ' . USERS_TABLE . ' u
		WHERE p.topic_id = ' . $topic_data['topic_id'] . '
			AND p.poster_id = u.user_id
		ORDER BY u.username_clean';
	$result = $db->sql_query($sql);
	$user_ary = array();
	while($urow = $db->sql_fetchrow($result))
	{
		$user_ary[] = '<option value="' . $urow['poster_id'] . '">' . $urow['username_clean'] . '</option>';
	}
	$s_topic_users = implode(' ', $user_ary);
	$db->sql_freeresult($result);
	$template->assign_vars(array(
		'S_SELECT_SORT_USERS'	=> '<select name="user_select">' . $s_topic_users . '</select>',
	));
//----[ End User Post Isolation ]----\\

